byTagName.map:
function(doc) {
  if (doc.type === 'question') {
    for (var i = 0; i < doc.tags.length; i++) {
      var tagName = doc.tags[i].Name;
      emit(tagName, doc);
    }
  }
}

latestQuestions.map:
function (doc) {
  if (doc.type === 'question' && doc.CreatedAt) {
    var dateStr = doc.CreatedAt.split("T");
    emit(dateStr[0], doc);
  }
}
